// Sample the Beckmann microfacet distribution
$(Ci) = samplepdf(	4, $(samples),
					0, 0, $(roughness), 0, 0,
					$(I), $(udir), $(N),
					$(label), $(subset), $(hitmode), $(bias),
					$(maxdist), $(envmap), $(envspace), $(filter),
					$(width), $(envsamples), $(envblur) );
aov_reflection += $(Ci);
