block.rsl_code:
/* make sure options are clear and set them according to shadeop * and renderer */ #ifdef SHADEOP_ARGUMENTS #undef SHADEOP_ARGUMENTS #endif
#if RENDERER == pixie #define SHADEOP_ARGUMENTS "filter", $(filter), \ /* can't specify s/tblur */ \ "blur", $(sblur), \ "swidth", $(swidth), "twidth", $(twidth), \ "fill", $(fill), "samples", $(samples), \ "bias", $(bias), "maxdist", $(maxdist), \ "samplecone", $(coneangle), "label", $(label) #elif RENDERER == _3delight #define SHADEOP_ARGUMENTS "sblur", $(sblur), "tblur", $(tblur), \ "swidth", $(swidth), "twidth", $(twidth), \ "samples", $(samples), "fill", $(fill), \ "filter", $(filter), "label", $(label), \ "subset", $(subset), "bias", $(bias), \ "hitmode", $(hitmode) #elif RENDERER == air #define SHADEOP_ARGUMENTS "bias", $(bias), \ /* can't specify s/blur */ \ "blur", $(sblur), "label", $(label), \ "maxdist", $(maxdist), "samples", $(samples), \ "subset", $(subset) #else #define SHADEOP_ARGUMENTS "sblur", $(sblur), "tblur", $(tblur), \ "swidth", $(swidth), "twidth", $(twidth), \ "samples", $(samples), "filter", $(filter), \ "fill", $(fill) #endif /* RENDERER */
// LDR expansion, from SIGGRAPH 2002 Course 16, // "Production Ready GI", by Hayden Landis #define $(blockname)_expandLDR $(expandLDR) #if $(blockname)_expandLDR == 1 varying $(value:type) $(blockname)_ctmp = $(Kr) * $(tint) * $(value:type) environment( $(texmap)[$(channel)], $(direction), SHADEOP_ARGUMENTS ); $(value) = envexpand( $(blockname)_ctmp, $(dynrange), $(startlum), $(exponent) ); #else // no expansion // tint is of float type if output variable is of float type $(value) = $(Kr) * $(tint) * $(value:type) environment( $(texmap)[$(channel)], $(direction), SHADEOP_ARGUMENTS ); #endif // expandLDR aov_reflection += $(value);
#undef $(blockname)_expandLDR #undef SHADEOP_ARGUMENTS

/* initialize */ $(Envcolor) = color(0); $(Envdirection) = vector(0); $(Irradiance) = color(0);
/* make sure options are clear */ /* and set them according to shadeop and renderer */ #ifdef SHADEOP_ARGUMENTS #undef SHADEOP_ARGUMENTS #endif /* SHADEOP_ARGUMENTS */
#if RENDERER == _3delight #define SHADEOP_ARGUMENTS "label", $(label), "subset", $(subset), \ "bias", $(bias), "hitmode", $(hitmode), \ "coneangle", $(coneangle), "axis", $(axis), \ "samplebase", $(samplebase), \ "maxdist", $(maxdist), \ "environmentmap", $(envmap), \ "environmentspace", $(envspace), \ "distribution", $(distribution), \ "environmentcolor", $(Envcolor), \ "environmentdir", $(Envdirection), \ "adaptive", $(adaptive), \ "minsamples", $(minsamples), \ "falloffmode", $(falloffmode), \ "falloff", $(falloff), "pointbased", $(useptc), \ "filename", $(ptcfile), "hitsides", $(hitsides), \ "maxsolidangle", $(maxsolidang), \ "clamp", $(clamp), \ "sortbleeding", $(sortbleeding), \ "coordsystem", $(coordsys) #elif RENDERER == pixie #define SHADEOP_ARGUMENTS "coordsystem", $(coordsys), \ "maxdist", $(maxdist), \ "coneangle", $(coneangle), \ "samplebase", $(samplebase), \ "label", $(label), "bias", $(bias), \ "maxerror", $(maxerror), \ "pointbased", $(useptc), \ "environmentmap", $(envmap), \ "filename", $(ptcfile), \ "maxpixeldist", $(maxpixeldist), \ "maxsolidangle", $(maxsolidang), \ "environmentcolor", $(Envcolor) #elif RENDERER == prman #define SHADEOP_ARGUMENTS "adaptive", $(adaptive), \ "minsamples", $(minsamples), \ "coordsystem", $(coordsys), \ "distribution", $(distribution), \ "maxdist", $(maxdist), \ "coneangle", $(coneangle), \ "samplebase", $(samplebase), \ "hitmode", $(hitmode), \ "hitsides", $(hitsides), "subset", $(subset), \ "label", $(label), \ "environmentmap", $(envmap), \ "environmentspace", $(envspace), \ "bias", $(bias), \ "maxpixeldist", $(maxpixeldist), \ "maxerror", $(maxerror), \ "falloffmode", $(falloffmode), \ "falloff", $(falloff), "pointbased", $(useptc), \ "filename", $(ptcfile), \ "maxsolidangle", $(maxsolidang), \ "clamp", $(clamp), \ "environmentcolor", $(Envcolor), \ "environmentdir", $(Envdirection) #elif RENDERER == air #define SHADEOP_ARGUMENTS "bias", $(bias), "label", $(label), \ "maxdist", $(maxdist), \ "maxerror", $(maxerror), \ "maxpixeldist", $(maxpixeldist), \ "samples", $(samples), "subset", $(subset) #elif RENDERER == aqsis #define SHADEOP_ARGUMENTS "blur", $(blur), "bias", $(bias) #else /* entropy, bmrt, renderdotc? */ #endif /* RENDERER */
/* different order of arguments */ #if RENDERER == _3delight || RENDERER == pixie || RENDERER == prman aov_occlusion += occlusion( $(P), $(direction), $(samples), SHADEOP_ARGUMENTS ); $(Occlusion) = 1 - aov_occlusion; #elif RENDERER == air /* shadow map based, unless map = "raytrace" */ aov_occlusion += occlusion( $(occlmap), $(P), $(direction), $(angle), SHADEOP_ARGUMENTS ); $(Occlusion) = 1 - aov_occlusion; #elif RENDERER == aqsis /* shadow map based occlusion */ aov_occlusion += occlusion( $(occlmap), $(P), $(direction), $(samples), SHADEOP_ARGUMENTS ); $(Occlusion) = 1 - aov_occlusion; #else /* entropy, bmrt, renderdotc? */ $(Occlusion) = 0; #endif /* RENDERER */

	#define use_qualitative_$(qualitative)
		aov_surfacecolor += $(Cdiff);

	#ifdef use_qualitative_1
		/* Air's diffuse can take a roughness parameter, in which */
		/* case behaves as the Oren-Nayar (qualitative?) model */
		#if RENDERER == air
			aov_diffuse += $(Kd) * diffuse( $(N), $(roughness) );
		#else
			aov_diffuse += $(Kd) * OrenNayar(	$(roughness), $(N), $(I),
												$(category) );
		#endif /* RENDERER */
        $(Ci) = aov_surfacecolor * aov_diffuse;
	#else
		aov_diffuse += $(Kd) * LG_OrenNayar( $(Cdiff), $(roughness), $(N),
												$(I), $(category) );
		$(Ci) = aov_diffuse;
	#endif /* use_qualitative_$(qualitative) */
	#undef use_qualitative_$(qualitative)
